mapper.sql:
where 1=1


        and a.id = #{id}


        and a.alias = #{alias}


        and a.agent_id = #{agentId}


        and a.geway_code_id = #{gewayCodeId}


        and a.channel_id = #{channelId}


        and a.binding_type = #{bindingType}


        and a.service_charge_type = #{serviceChargeType}


        and a.is_enable = #{isEnable}


        and b.agent_name = #{agentName}


        and c.geway_code_name = #{gewayCodeName}


        and d.channel_name = #{channelName}


        and a.deploy_id = #{deployId}


        and a.id not in

            #{item}


    and a.yn = 0

mapper.select:
select
from tb_pr_agent_profit_distribution a left join tb_pr_agent b on a.agent_id = b.id left join tb_pr_geway_code c on a.geway_code_id = c.id left join tb_pr_channel d on a.channel_id = d.id left join tb_pr_geway e on c.geway_id = e.id left join tb_pr_platform_geway_code f on a.pf_geway_code_id = f.id
order by a.create_time desc limit #{startIndex},#{pageSize}
